const isWindows = require('bin-links/lib/is-windows.js')
const getPrefix = require('bin-links/lib/get-prefix.js')
const getNodeModules = require('bin-links/lib/get-node-modules.js')
const { dirname } = require('path')

module.exports = ({ top, path }) =>
  !top ? getNodeModules(path) + '/.bin'
  : isWindows ? getPrefix(path)
  : dirname(getPrefix(path)) + '/bin'
