const rpj = require('read-package-json-fast')
const runScriptPkg = require('@npmcli/run-script/lib/run-script-pkg.js')
const validateOptions = require('@npmcli/run-script/lib/validate-options.js')
const isServerPackage = require('@npmcli/run-script/lib/is-server-package.js')

const runScript = options => {
  validateOptions(options)
  const { pkg, path } = options
  return pkg ? runScriptPkg(options)
    : rpj(path + '/package.json')
      .then(readPackage => runScriptPkg({ ...options, pkg: readPackage }))
}

module.exports = Object.assign(runScript, { isServerPackage })
